pragma solidity ^0.4.19;

interface tokenRecipient {
  function receiveApproval(
    address _from, uint256 _value, address _token, bytes _extraData
  ) external;
}

contract REToken {
    // Public variables of the token
    uint8 public decimals = 18;
    // 18 decimals is the strongly suggested default, avoid changing it
    uint256 public totalSupply;               // How many decimals to show. To be standard complicant keep it 18
    string public symbol = "RET";
    uint256 public retokenPrice = 200000000000000000;       // 0.2 ether

    // This creates an array with all balances
    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This generates a public event on the blockchain that will notify clients
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);

    /**
     * Constructor function
     *
     * Initializes contract with initial supply tokens to the creator of the contract
     */
    function REToken(uint256 initialSupply) public {
        totalSupply = initialSupply * 10 ** uint256(decimals);        // Update total supply with the decimal amount
        balanceOf[this] = totalSupply;                                // Set the symbol for display purposes
    }

    /**
     * Internal transfer, only can be called by this contract
     */
    function _transfer(address _from, address _to, uint _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != 0x0);
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        // Save this for an assertion in the future
        uint previousBalances = balanceOf[_from] + balanceOf[_to];
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        Transfer(_from, _to, _value);
        // Asserts are used to use static analysis to find bugs in your code. They should never fail
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    /**
     * Transfer tokens
     *
     * Send `_value` tokens to `_to` from your account
     *
     * @param _to The address of the recipient
     * @param _value the amount to send
     */
    function transfer(address _to, uint256 _value) public returns (bool success) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    /**
     * Transfer tokens from other address
     *
     * Send `_value` tokens to `_to` on behalf of `_from`
     *
     * @param _from The address of the sender
     * @param _to The address of the recipient
     * @param _value the amount to send
     */
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);     // Check allowance
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    /**
     * Set allowance for other address
     *
     * Allows `_spender` to spend no more than `_value` tokens on your behalf
     *
     * @param _spender The address authorized to spend
     * @param _value the max amount they can spend
     */
    function approve(address _spender, uint256 _value) public
        returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        Approval(msg.sender, _spender, _value);
        return true;
    }

    function buy() payable public returns (uint amount) {
        amount = msg.value / retokenPrice;
        require(balanceOf[this] >= amount);
        _transfer(this, msg.sender, amount);
        return amount;
    }

    function sell(uint amount) public returns (uint revenue){
        require(balanceOf[msg.sender] >= amount);
        _transfer(msg.sender, this, amount);
        revenue = amount * retokenPrice;
        require(msg.sender.send(revenue));
        return revenue;
    }
}

contract EscrowSmartContract {
  REToken retoken;

  struct Pledge {
    uint id;
    address renter;
    address houseowner;
    uint amount;
    bool pickedUp;
  }

  mapping (uint => Pledge) public pledges;
  uint pledgeCounter;

  //events
  event LogPutInPledge(
    uint indexed _id,
    address indexed _renter,
    address indexed _houseowner,
    uint _amount
  );
  event LogPickUpPledge(
    uint indexed _id,
    address indexed _renter,
    address indexed _houseowner,
    uint _amount
  );

  function EscrowSmartContract(address _RETokenAddress) public {
    retoken = REToken(_RETokenAddress);
  }

  function putInPledge(uint _amount, address _receiver) public {
    require(retoken.allowance(msg.sender, address(this)) >= _amount);
    require(retoken.transferFrom(msg.sender, address(this), _amount));

    pledgeCounter++;

    pledges[pledgeCounter] = Pledge(
      pledgeCounter,
      msg.sender,
      _receiver,
      _amount,
      false
    );

    LogPutInPledge(pledgeCounter, msg.sender, _receiver, _amount);
  }

  function pickUpPledge(uint _id) public {
    require(_id > 0 && _id <= pledgeCounter);
    Pledge storage pledge = pledges[_id];
    require(pledge.houseowner == msg.sender);
    require(pledge.pickedUp == false);

    require(retoken.transfer(msg.sender, pledge.amount));

    pledge.pickedUp = true;

    LogPickUpPledge(_id, pledge.renter, msg.sender, pledge.amount);
  }

  //returns pledges ids, putted by msg.sender
  function getAvailablePledges() public view returns (uint[]) {
    uint[] memory pledgesId = new uint[](pledgeCounter);
    uint numberOfAvailablePledges = 0;

    for (uint i = 1; i <= pledgeCounter; i++){
      if (pledges[i].pickedUp == false){
        pledgesId[numberOfAvailablePledges] = pledges[i].id;
        numberOfAvailablePledges++;
      }
    }

    uint[] memory availablePledgeIds = new uint[](numberOfAvailablePledges);
    for(uint j = 0; j < numberOfAvailablePledges; j++){
      availablePledgeIds[j] = pledgesId[j];
    }

    return availablePledgeIds;
  }
}
